import React ,{useEffect} from 'react';
import {useDispatch,useSelector} from 'react-redux';
import {Row,Col,Button,Image,ListGroup,ListGroupItem } from "react-bootstrap";
import {listProductsDetails} from '../actions/productActions'
import {useParams} from "react-router-dom";
import Rating from '../Components/Rating';
import {Link} from 'react-router-dom';


const ProductDetails = ({match}) => {
  
  const dispatch=useDispatch();
  const productDetails= useSelector(state=>state.productDetails)
  const {id} = useParams();
  const {loading,error,product}=productDetails;
    useEffect(()=>{
      dispatch(listProductsDetails(id))
    },[dispatch,match]);


    return (
    <div>
      <Link to='/' className='btn'>
        <i class="fa fa-arrow-left" aria-hidden="true"></i>
        Go Back</Link>
        <Row>
        <Col md={6}>
        <Image src={product.image} alt={product.name } fluid ></Image>
        </Col> 
        <Col md ={3}>
          <ListGroup variant='flush'>
            <ListGroupItem>
              <h3>{product .name}</h3>
            </ListGroupItem>
            <ListGroupItem>
              <Rating value={product.rating} text={`${product.numReviews} Reviews`}/>
            </ListGroupItem>
            <ListGroupItem>Price: ${product.price}</ListGroupItem>
            <ListGroupItem>{product.description}</ListGroupItem>
          </ListGroup>
        </Col>
        <Col md={3}>
          <ListGroupItem>
            <Row>
              <Col>Status :</Col>
              <Col >{product.countInStock >0 ? `${product.countInStock} In stock`:'out of stock'}</Col>
            </Row>
          </ListGroupItem>
          <ListGroupItem>
              <Button className='btn-block my-3 ' type='button'>Add to Cart</Button>
          </ListGroupItem>
        </Col>
        </Row>
    </div>
  )
}

export default ProductDetails
